.save-page {
	cursor:pointer;
}

.save-page.saved {
	cursor:default;
}

.save-page .icon {
	width:24px;
	height:24px;
	float:left;
	display:block;	
	background: url(/Images/save-pages-sprite.png) 0 -100px no-repeat;
	margin:10px 6px 0 10px;
}

.save-page.contensis {
	width:210px;
	height:41px;
	display:block;
	background: url(/Images/save-pages-sprite.png) 0 0 no-repeat;
	color:#8cc63f;
}

.save-page.be-mac {
	width:180px;
	height:41px;
	display:block;
	background: url(images/save-pages-sprite.png) 0 -50px no-repeat;
	color:#8cc63f;
}

.save-page .save-to {
	font-family:"Avenir-Medium", Arial, Helvetica, sans-serif;
	font-size:14px;
	width:130px;
	float:left;
	margin-top:4px;
	line-height:16px;
}

.save-page .save-to .my-mac {
	font-family:"Macmillan_Headline_Regular", Arial, Helvetica, sans-serif;
	font-size:16px;
	float:left;
	color:#fff;
}

.save-page:hover .save-to, 
.save-page:hover .save-to .my-mac {
	text-decoration:underline;
	color:#8cc63f;
}

.save-page:hover .icon {
	background-position:0 -150px;
}

.save-page.saved .icon {
	background-position:0 -200px;
}

.save-page.saved:hover .save-to {
	text-decoration:none;
	color:#8cc63f;
}

.save-page.saved:hover .save-to .my-mac {
	text-decoration:none;
	color:#fff;
}



