        $(document).ready(function () {
            var listItem = $('.page-listing'),
                listingCheckbox = $(".page-listing input[type='checkbox']"),
                listingAll = $(".page-actions input.selectall_checkbox");
            $(".page-actions li:not(:first-child)").find('a span').addClass('js-disabled-actions');
            var disabled = $('.js-disabled-actions');
            var actionLink = $('.page-actions a');



            /* List Item checkbox */
            $(listingCheckbox).each(function () {
                $(this).on('click', function () {
                    if (this.checked == true) {
                        $(this).closest(listItem).addClass('js-selectedList');
                        $('.page-actions').removeClass('disabled-actions');
                        $(".page-actions li:not(:first-child)").find('a span').removeClass('js-disabled-actions');
                        $(".page-actions li:not(:first-child)").find('a span').addClass('js-enabled-actions');
                    }
                    else {
                        $(this).closest(listItem).removeClass('js-selectedList');
                        if ($('.js-selectedList').length === 0) {
                            $('.page-actions').addClass('disabled-actions');
                            $(".page-actions li:not(:first-child)").find('a span').addClass('js-disabled-actions');
                            $(".page-actions li:not(:first-child)").find('a span').removeClass('js-enabled-actions');
                        }
                    }
                });

            });

            /* Select All checkbox */
            $(listingAll).on('click', function () {
                if (this.checked == true) {
                    $(this).parentsUntil('.listing').siblings('.page-listing').addClass('js-selectedList');
                    $('.page-actions').removeClass('disabled-actions');
                    $(".page-actions li:not(:first-child)").find('a span').removeClass('js-disabled-actions');
                    $(".page-actions li:not(:first-child)").find('a span').addClass('js-enabled-actions');
                }
                else {
                    $(this).parentsUntil('.listing').siblings('.page-listing').removeClass('js-selectedList');
                    if ($('.js-selectedList').length === 0) {
                        $('.page-actions').addClass('disabled-actions');
                        $(".page-actions li:not(:first-child)").find('a span').addClass('js-disabled-actions');
                        $(".page-actions li:not(:first-child)").find('a span').removeClass('js-enabled-actions');
                    }
                }
            });


            if (disabled.length) {
                $('.page-actions').addClass('disabled-actions');
                $(actionLink).each(function () {
                    $(this).on('click', function () {
                        return false;
                    });
                });
            }
        });


        /*IE 7 Workaround for trim*/
        if (typeof String.prototype.trim !== 'function') {
            String.prototype.trim = function () {
                return this.replace(/^\s+|\s+$/g, '');
            }
        }

        $("input[type='text']").focusout(function () {
            var me = $(this);
            var text = me.val().trim();
            me.val(text);
        });


        $('#titleDropdown').selectmenu();

